#
# Procedure "evaluate_order"
#

DROP PROCEDURE IF EXISTS `evaluate_order`;
CREATE PROCEDURE `evaluate_order`(
	in `p_order_id` int(8),
	in `p_post_doctor_id` int(6),
	in `p_evaluation_level` varchar(3),
	in `p_evaluation_detail` varchar(255),
  in `p_speed_level` varchar(3),
	in `p_professional_level` varchar(3),
	in `p_patience_level` varchar(3),
	in `p_practical_level` varchar(3),
	out code varchar(10)
	)
BEGIN
  declare v_take_doctor_id int(8);/*接单医生*/
  declare v_money float(4,2); /* 订单奖金 */
  declare v_account_id int(6);/* 账户id */
  DECLARE EXIT HANDLER FOR SQLEXCEPTION 
  begin
       rollback;
       set code = '1';
  end;
  start transaction;
  set code = '0';
	/* 将评价详情更新 */
	update order_detail set evaluation_level = p_evaluation_level, evaluation_detail = p_evaluation_detail, 
						speed_level = p_speed_level, professional_level = p_professional_level, practical_level = p_practical_level ,order_end_time = now()
						where order_id = p_order_id;
    /* --查询订单情况 */
	select take_doctor_id,money into v_take_doctor_id,v_money from orders where order_id = p_order_id;
	/* --更新医生账户 */
	select account_id into v_account_id from account where user_id = v_take_doctor_id;
	insert into account_record (account_id,income,activity_time,order_id) values (v_account_id,v_money,now(),p_order_id);
	update account set balance = (balance+v_money) where account_id = v_account_id;
	
  commit;
END;

#
# Procedure "post_order"
#

DROP PROCEDURE IF EXISTS `post_order`;
CREATE PROCEDURE `post_order`(
	in `order_name` varchar(30),
	in `post_doctor_id` int(6),
	in `disease_type` varchar(3),
	in `order_description` varchar(500),
  in `file_id` varchar(50),
  in `money` float(4,2),
	out code varchar(10)
	)
BEGIN
  declare order_id int(8);
  DECLARE EXIT HANDLER FOR SQLEXCEPTION 
  begin
       rollback;
       set code = '1';
  end;
  start transaction;
  set code = '0';
	insert into orders (order_name,post_doctor_id,disease_type,file_id,order_start_time,money) values (order_name,post_doctor_id,disease_type,file_id,now(),money);
	SELECT LAST_INSERT_ID() into order_id;
	insert into order_detail (order_id,order_description) values (order_id,order_description);
   select order_id;
  commit;
END;
